property.value:
# This is a simple actor that copies the input to the output.
# You can remove the ports, add new ports, and modify the script.
class Main :
  "Copy the inputs to the output."
  def fire(self) :
    token = self.input.get(0)
    h = token.stringValue() 			# get the path from $WORK_DIr
    h = h[1:len(h)-1] 			# remove surrounding quotes
    f = open('/'+h+'/_CLUSTER_TRAJ_FIRST_FRAME.pdb', 'r')
    x = open('/'+h+'/_CLUSTER_TRAJ_FIRST_FRAME_MODIFIED.pdb', 'w')

    for line in f:
       if line.count("MODEL ") > 0:
         continue
       elif line.count("END ") > 0:
         continue
       else: 
         x.write(line)

    f.close()
    x.close()

    a = open('/'+h+'/_CLUSTER_TRAJ_SELECTED_FRAMES.pdb', 'r')
    b = open('/'+h+'/_CLUSTER_TRAJ_SELECTED_FRAMES_MODIFIED.pdb', 'w') 

    for i in a:
       if i.count("MODEL ") > 0:
         continue
       elif i.count("END ") > 0:
         continue
       else: 
         b.write(i)

    a.close()
    b.close()

    self.output.broadcast(token)
    return

